<div <?php if(empty($_GET['context']) || $_GET['context'] !== 'edit') echo get_block_wrapper_attributes( array('class' => "col-lg-6", ) ); else echo 'data-wp-block-props="true"'; ?>>
    <a href="#" class="d-block event-link px-4" data-pg-ia-hide> <?php if ( !PG_Blocks_v3::getImageSVG( $args, 'event_image', false) && PG_Blocks_v3::getImageUrl( $args, 'event_image', 'full' ) ) : ?><img src="<?php echo PG_Blocks_v3::getImageUrl( $args, 'event_image', 'full' ) ?>" class="<?php echo (PG_Blocks_v3::getImageField( $args, 'event_image', 'id', true) ? ('wp-image-' . PG_Blocks_v3::getImageField( $args, 'event_image', 'id', true)) : '') ?> img-fluid mb-4" alt="<?php echo PG_Blocks_v3::getImageField( $args, 'event_image', 'alt', true); ?>"/><?php endif; ?><?php if ( PG_Blocks_v3::getImageSVG( $args, 'event_image', false) ) : ?><?php echo PG_Blocks_v3::mergeInlineSVGAttributes( PG_Blocks_v3::getImageSVG( $args, 'event_image' ), array( 'class' => 'img-fluid mb-4' ) ) ?><?php endif; ?> <h3 class="h4"><?php echo PG_Blocks_v3::getAttribute( $args, 'event_title' ) ?></h3> </a>
</div>